import "./App.css";
import React from "react";
import {Routes, Route} from "react-router-dom";
import HomePage from "./pages/HomePage";
import Search from "./pages/Search";
import { useState, useEffect } from "react";
import * as BooksAPI from "./BooksAPI";

function App() {


  const [books, setBooks] = useState([]);

  useEffect(()=>{
    const getBooks= async ()=>{
     const books = await BooksAPI.getAll();
      setBooks(books);
    }; 
    getBooks();
  },[]);


  // const updateBook = (book, shelf) => {
  //   const update= async () => {
  //     const res = await BooksAPI.update(book, shelf);
  //     setBooks(books.concat(res));
  //   };

  //   update();
  // };

  changeShelf = async (book, shelf) => {
    await BooksAPI.update(book, shelf);
    await BooksAPI.getAll().then((res) => {
      this.setState({
        books: res,
      });
    });
    this.handleBooksSearch(this.state.search)
  };


  return (
    <div className="app">
       <Routes>
        <Route exact path={"/"} element={<HomePage books={books} updateBook={updateBook} />} />
        <Route path={"/search"} element={<Search books={books}  updateBook={updateBook}/>} />
      </Routes> 
    </div>
  );
}

export default App;
